    @Test
    public void parseSteamUser() {
        String json = "{\"steamid\":\"76561198085657484\",\"communityvisibilitystate\":3,\"profilestate\":1,\"personaname\":\"Mr. Seth X3\",\"lastlogoff\":1521361398,\"commentpermission\":1,\"profileurl\":\"http://steamcommunity.com/id/SethsUtopia/\",\"avatar\":\"https://steamcdn-a.akamaihd.net/steamcommunity/public/images/avatars/da/da2fc3f10df50bc529f3cf3b4898eb186595e7de.jpg\",\"avatarmedium\":\"https://steamcdn-a.akamaihd.net/steamcommunity/public/images/avatars/da/da2fc3f10df50bc529f3cf3b4898eb186595e7de_medium.jpg\",\"avatarfull\":\"https://steamcdn-a.akamaihd.net/steamcommunity/public/images/avatars/da/da2fc3f10df50bc529f3cf3b4898eb186595e7de_full.jpg\",\"personastate\":1,\"realname\":\"Seth\",\"primaryclanid\":\"103582791429521408\",\"timecreated\":1362733966,\"personastateflags\":0,\"loccountrycode\":\"GB\",\"locstatecode\":\"A5\",\"loccityid\":16536}";
        JSONObject object = new JSONObject(json);
        SteamUser user = new SteamUser(null, object);

        assertAll("Ensure Parsing Result Data Correctly",
            () -> assertEquals(null, user.getSteam()),
            () -> assertEquals(76561198085657484L, user.getId()),
            () -> assertEquals(false, user.isPrivate()),
            () -> assertEquals("Mr. Seth X3", user.getUsername()),
            () -> assertEquals("Seth", user.getRealName()),
            () -> assertEquals("https://steamcdn-a.akamaihd.net/steamcommunity/public/images/avatars/da/da2fc3f10df50bc529f3cf3b4898eb186595e7de_full.jpg", user.getAvatar()),
            () -> assertEquals("http://steamcommunity.com/id/SethsUtopia/", user.getProfileURL()),
            () -> assertEquals("GB", user.getCountryCode()),
            () -> assertEquals(1521361398, user.getLastLogOff().getEpochSecond()),
            () -> assertEquals(PersonaState.ONLINE, user.getPersonaState()),
            () -> assertEquals("103582791429521408", user.getPrimaryClanId()),
            () -> assertEquals("A5", user.getStateCode()),
            () -> assertEquals(1362733966, user.getTimeCreated().getEpochSecond()),
            () -> assertEquals(true, user.hasProfile())
        );
    }

    @Test
    public void parseGame() {
        Steam steam = new Steam("DCA56FE963FE3D2CE23DF7DF30AAA579");

        String json = "{\"steamid\":\"76561198085657484\",\"communityvisibilitystate\":3,\"profilestate\":1,\"personaname\":\"Mr. Seth X3\",\"lastlogoff\":1521361398,\"commentpermission\":1,\"profileurl\":\"http://steamcommunity.com/id/SethsUtopia/\",\"avatar\":\"https://steamcdn-a.akamaihd.net/steamcommunity/public/images/avatars/da/da2fc3f10df50bc529f3cf3b4898eb186595e7de.jpg\",\"avatarmedium\":\"https://steamcdn-a.akamaihd.net/steamcommunity/public/images/avatars/da/da2fc3f10df50bc529f3cf3b4898eb186595e7de_medium.jpg\",\"avatarfull\":\"https://steamcdn-a.akamaihd.net/steamcommunity/public/images/avatars/da/da2fc3f10df50bc529f3cf3b4898eb186595e7de_full.jpg\",\"personastate\":1,\"realname\":\"Seth\",\"primaryclanid\":\"103582791429521408\",\"timecreated\":1362733966,\"personastateflags\":0,\"loccountrycode\":\"GB\",\"locstatecode\":\"A5\",\"loccityid\":16536}";
        JSONObject object = new JSONObject(json);
        SteamUser user = new SteamUser(steam, object);

        json = "{\"appid\":578080,\"name\":\"PLAYERUNKNOWN'S BATTLEGROUNDS\",\"playtime_forever\":613,\"img_icon_url\":\"93d896e7d7a42ae35c1d77239430e1d90bc82cae\",\"img_logo_url\":\"2d2732a33511b58c69aff6b098a22687a3bb8533\",\"has_community_visible_stats\":true}";
        object = new JSONObject(json);
        SteamGame game = new SteamGame(user, object);

        assertAll("Ensure Parsing Result Data Correctly",
            () -> assertEquals(steam, game.getSteam()),
            () -> assertEquals(user, game.getUser()),
            () -> assertEquals(578080, game.getId()),
            () -> assertEquals("PLAYERUNKNOWN'S BATTLEGROUNDS", game.getName()),
            () -> assertEquals("http://store.steampowered.com/app/578080", game.getGameUrl()),
            () -> assertEquals(10, game.getTotalPlaytime()),
            () -> assertEquals(0, game.getRecentPlaytime()),
            () -> assertEquals("http://media.steampowered.com/steamcommunity/public/images/apps/578080/93d896e7d7a42ae35c1d77239430e1d90bc82cae.jpg", game.getIconUrl()),
            () -> assertEquals("http://media.steampowered.com/steamcommunity/public/images/apps/578080/2d2732a33511b58c69aff6b098a22687a3bb8533.jpg", game.getLogoUrl()),
            () -> assertEquals(true, game.isStatsVisible())
        );
    }

