    @Test
    public void parseNoResults() {
        String json = "{\"tags\":[],\"result_type\":\"no_results\",\"list\":[],\"sounds\":[]}";
        JSONObject object = new JSONObject(json);
        UrbanResult result = new UrbanResult(object, "iohwefiwhofhweohfowief");

        assertAll("Ensure Parsing No Result Data Correctly",
            () -> assertEquals("iohwefiwhofhweohfowief", result.getSearchTerm()),
            () -> assertEquals(UrbanResultType.NO_RESULTS, result.getResultType()),
            () -> assertNull(result.getSounds())
        );
    }

