    @Test
    public void parseGameSessionData() throws IOException {
        server.enqueue(new MockResponse().setBody("{\"response\":{\"players\":[{\"steamid\":\"76561198059321490\",\"communityvisibilitystate\":3,\"profilestate\":1,\"personaname\":\"Liental\",\"lastlogoff\":1530293627,\"profileurl\":\"https://steamcommunity.com/profiles/76561198059321490/\",\"avatar\":\"https://steamcdn-a.akamaihd.net/steamcommunity/public/images/avatars/90/903924fd9d443f43e7b121d085062fbdd2064f25.jpg\",\"avatarmedium\":\"https://steamcdn-a.akamaihd.net/steamcommunity/public/images/avatars/90/903924fd9d443f43e7b121d085062fbdd2064f25_medium.jpg\",\"avatarfull\":\"https://steamcdn-a.akamaihd.net/steamcommunity/public/images/avatars/90/903924fd9d443f43e7b121d085062fbdd2064f25_full.jpg\",\"personastate\":1,\"realname\":\".\",\"primaryclanid\":\"103582791439623167\",\"timecreated\":1330099039,\"personastateflags\":0,\"gameserverip\":\"172.107.94.110:27015\",\"gameserversteamid\":\"85568392920131863\",\"gameextrainfo\":\"Garry's Mod\",\"gameid\":\"4000\"}]}}"));

        GameSession session = steam.getUsers(76561198059321490L).complete().get(0).getSession();
        assertAll("Ensure Parsing Result Data Correctly",
            () -> assertEquals(4000, session.getGameId()),
            () -> assertEquals("Garry's Mod", session.getGameStatus()),
            () -> assertEquals(85568392920131863L, session.getServerId()),
            () -> assertEquals("172.107.94.110:27015", session.getServerAddress())
        );
    }

